import './Cards.css';
import{useEffect, useState} from "react";
import CardItemList from "../CardItemList";
const Cards = () => {
    const [cards, setCards] = useState([]);
    const [loading, setLoading] = useState(false);
    const [busqueda, setBusqueda] = useState();

    useEffect(() => {
        setLoading(true);
        fetch('http://localhost:8080/apicartas/cartas')
        .then(response => response.json())
        .then(cards => setCards(cards))
        .then(() => setLoading(false));
        
    }, []);
    function search(rows){
        return cards.filter(cards => cards.nombre.lowercase.indexOf(busqueda));
    }
    if(loading){
        return <h2>Loading....</h2>
    }
    return (
        
        <div className="cards">
            <div className="searchBar">
                <input type="text" value={busqueda} placeholder="Buscar..." onChange={(e) => setBusqueda(e.target.value)}/>
            </div>0
            <h1 class="d-flex justify-content-center">Cartas</h1>
            <CardItemList cards={search(cards)} />
        </div>
    );
}
export default Cards;
