import React from "react";
import { cartContext } from '../../Context/CartContext';
import { useContext } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Navbar, Nav } from "react-bootstrap";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserPlus, faSignInAlt, faSignOutAlt } from "@fortawesome/free-solid-svg-icons";
import { logoutUser } from "../../Services/index";
import { DropdownButton, Dropdown } from "react-bootstrap";
import './Header.css';

const Header = () => {
    const auth = useSelector((state) => state.auth);
    const dispatch = useDispatch();
    const cont = useContext(cartContext);
    const logout = () => {
        dispatch(logoutUser());
    };
    const user = localStorage.getItem("user");

    const guestLinks = (
        <>
            <Nav className="navbar-right row" class="registerLogin">

                <Link to={"/signup"} className="nav-link d-flex col align-items-center">
                    <FontAwesomeIcon className="nav-link p-1" width="50px" height="50px" icon={faUserPlus} />
                    <h2 className="col-6-md">Registrate</h2>
                </Link>
            </Nav>
            <Nav className="registerLogin navbar-right">
                <Link to={"/login"} className="nav-link d-flex col align-items-center">
                    <FontAwesomeIcon icon={faSignInAlt} className="nav-link mr-2" class="icon" />
                    <h2 className="col-md-6">Login</h2>
                </Link>
            </Nav>
        </>
    );
    const userLinks = (
        <>
            <Navbar >
                <DropdownButton id="dropdown-basic-button" title={user} class="dropdown">
                    <Link to={"/account"}>
                        <Dropdown.Item href="/account">Cuenta</Dropdown.Item>
                    </Link>
                </DropdownButton>
                <Nav className="navbar-right" class="logout">
                    <Link to={"/logout"} className="nav-link d-flex justify-content-between" onClick={logout}>
                        <h2><FontAwesomeIcon icon={faSignOutAlt} />Cerrar sesi√≥n</h2>
                    </Link>
                </Nav>
                <Nav class="carrito">
                    <Link to="/shoppingCart" className='nav-link'>
                        <div className='position-relative btn btn-outline-dark'>Carrito<i className='fas fa-cart'></i> {cont.cart.length > 0 && <div className="position-absolute top-25 start-100 translate-middle badge rounded-pill bg-danger">{cont.cart.length}</div>}</div></Link>
                </Nav>
            </Navbar>

        </>
    );
    return (
        <header class="header">
            <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
                <div class="container-fluid">
                    <Link to={auth.isLoggedIn ? "home" : ""}>
                        <h1 class="tcgMarket">TCG Market!</h1>
                    </Link>
                    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarColor02" aria-controls="navbarColor02" aria-expanded="false" aria-label="Toggle navigation">
                        <span class="navbar-toggler-icon"></span>
                    </button>

                    <div class="collapse navbar-collapse" id="navbarColor02">
                        <ul class="navbar-nav me-auto">
                            <li class="nav-item active">
                                <a class="nav-link" href="#">Home
                                    <span class="visually-hidden">(current)</span>
                                </a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">Features</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">Pricing</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">About</a>
                            </li>
                            <li class="nav-item dropdown">
                                <a class="nav-link dropdown-toggle" data-bs-toggle="dropdown" href="#" role="button" aria-haspopup="true" aria-expanded="false">Dropdown</a>
                                <div class="dropdown-menu">
                                    <a class="dropdown-item" href="#">Action</a>
                                    <a class="dropdown-item" href="#">Another action</a>
                                    <a class="dropdown-item" href="#">Something else here</a>
                                    <div class="dropdown-divider"></div>
                                    <a class="dropdown-item" href="#">Separated link</a>
                                </div>
                            </li>
                        </ul>
                        <form class="d-flex">
                            <input class="form-control me-sm-2" type="text" placeholder="Search" />
                            <button class="btn btn-secondary my-2 my-sm-0" type="submit">Search</button>
                        </form>
                    </div>
                </div>
            </nav>
        </header>
    );
};

export default Header;
