import './Cards.css';
import{useEffect, useState} from "react";
import CardItemList from "../CardItemList";
import Pagination from "../../PaginationComponent/Pagination";
import Search from '../../SearchComponent/Search';
const Cards = () => {

    const [cards, setCards] = useState([]);
    const [loading, setLoading] = useState(false);
    const [busqueda, setBusqueda] = useState('');
    const [cardsPerPage, setCardsPerPage] = useState(4);
    const [currentPage, setCurrentPage] = useState(1);
    const [state, setState] = useState({
        results: []
    });
    const onSearch = async (text) => {
        const results = fetch(`http://localhost:8080/apicartas/cartas/buscarPorNombre/${text}`)
        .then(response => response.json())
        .then(cards => setCards(cards))
        .then(() => setLoading(false));
        setState(prevState => {
            return { ...prevState, results: results }
        });
    };
    
       
    useEffect(() => {
        setLoading(true)
        fetch('http://localhost:8080/apicartas/cartas')
        .then(response => response.json())
        .then(cards => setCards(cards))
        .then(() => setLoading(false));
    }, []);
    
    const indexOfLastCard = currentPage * cardsPerPage;
    const indexOfFirstCard = indexOfLastCard - cardsPerPage;
    const currentCards = results.slice(indexOfFirstCard, indexOfLastCard);
    
    const paginate = pageNumber => setCurrentPage(pageNumber);
        
        return (
            <div className="cards">
                <Search onSearch={onSearch}/>
                <h1 class="d-flex justify-content-center">Cartas</h1>
                <CardItemList cards={currentCards}/>
                <Pagination
                class="p-3"
                cardsPerPage={cardsPerPage}
                totalCards={cards.length}
                paginate={paginate}
                />
            </div>
        );
}

export default Cards;
