import CardItem from '../CardItem';
import './CardItemList.css';
import CardGroup from 'react-bootstrap/CardGroup';
import {cartContext} from '../../../Context/CartContext';
const CardItemList = ({cards = []}) => {
    return (
        <div className="cardItemList" class="row">
            <CardGroup>
                {cards.map(card => <CardItem key={card.id} card={card} onComprar={(card, cantidad) => cartContext.setCart([...cartContext.carrito,{ ...card, cantidad },])}/>)}
            </CardGroup>
        </div>
    );
}

export default CardItemList;
