import React from "react";
import { cartContext } from '../../Context/CartContext';
import { useContext } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Navbar, Nav } from "react-bootstrap";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserPlus, faSignInAlt, faSignOutAlt } from "@fortawesome/free-solid-svg-icons";
import { logoutUser } from "../../Services/index";
import { DropdownButton, Dropdown } from "react-bootstrap";
import './Header.css';

const Header = () => {
    const auth = useSelector((state) => state.auth);
    const dispatch = useDispatch();
    const cont = useContext(cartContext);
    const logout = () => {
        dispatch(logoutUser());
    };
    const user = localStorage.getItem("user");
    const guestLinks = (
        <>
            <Nav className="navbar-right row" class="registerLogin">

                <Link to={"/signup"} className="nav-link d-flex col">
                    <FontAwesomeIcon className="nav-link" width="50" height="50" icon={faUserPlus} />
                    <h2 className="col-6-md">Registrate</h2>
                </Link>
            </Nav>
            <Nav className="registerLogin navbar-right">
                
                <Link to={"/login"} className="nav-link col">
                <FontAwesomeIcon icon={faSignInAlt} className="nav-link" class="icon" />
                    <h2 className="col-md-6">Login</h2>
                </Link>
            </Nav>
        </>
    );
    const userLinks = (
        <>
            <Navbar >
                <DropdownButton id="dropdown-basic-button" title={user} class="dropdown">
                    <Link to={"/account"}>
                        <Dropdown.Item href="/account">Cuenta</Dropdown.Item>
                    </Link>
                </DropdownButton>
                <Nav className="navbar-right" class="logout">
                    <Link to={"/logout"} className="nav-link d-flex justify-content-between" onClick={logout}>
                        <h2><FontAwesomeIcon icon={faSignOutAlt} />Cerrar sesión</h2>
                    </Link>
                </Nav>
                <Nav class="carrito">
                    <Link to="/shoppingCart" className='nav-link'>
                        <div className='position-relative btn btn-outline-dark'>Carrito<i className='fas fa-cart'></i> {cont.cart.length > 0 && <div className="position-absolute top-25 start-100 translate-middle badge rounded-pill bg-danger">{cont.cart.length}</div>}</div></Link>
                </Nav>
            </Navbar>

        </>
    );

    return (
        <header class="header">
            <Navbar>
                <Link to={auth.isLoggedIn ? "home" : ""} className="navbar-brand">
                    <h1 class="tcgMarket">TCG Market!</h1>
                </Link>
                {auth.isLoggedIn ? userLinks : guestLinks}
            </Navbar>
        </header>
    );
};

export default Header;
