import './CardInfo.css';
import { useParams} from 'react-router-dom';
import { useRef, useState, useEffect, useContext } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ListGroupItem from 'react-bootstrap/ListGroupItem';
import ListGroup from 'react-bootstrap/ListGroup';
const CardInfo = () => {
    const {cartaId} = useParams();
    useEffect(() => {
        fetch(`http://localhost:8080/apicartas/cartas/${cartaId}`)
        .then(response => response.json())
        .then(card => setCard(card));
    }, []);
    const [card, setCard] = useState({});
    return (
        <div className="cardInfo" class="col-lg-3">
            <Card style={{ width: '18rem' }}>
                <Card.Img variant="left" src={card.imagen}/>
                <Card.Body>
                    <Card.Title>{card.nombre}</Card.Title>
                    <ListGroup className="list-group-flush">7
                        <ListGroupItem>Codigo: {card.codigo}</ListGroupItem>
                        <ListGroupItem>Precio: {card.precio}â‚¬</ListGroupItem>
                        <ListGroupItem>Reprint: {
                        card.reprint === true ? card.reprint : 'Si'|| card.reprint === false ? card.reprint : 'No'
                        }</ListGroupItem>
                        <ListGroupItem>Palabras Clave: {card.keywords}</ListGroupItem>
                        <ListGroupItem>Descripcion: {card.descripcion}</ListGroupItem>
                        <ListGroupItem>Texto: {card.texto}</ListGroupItem>
                    </ListGroup>
                    <Card.Footer className="d-flex flex-column">
                        <Button variant="primary" className="pt-2" href="" >Comprar</Button>
                    </Card.Footer>
                </Card.Body>
            </Card>
        </div>
    );
};

export default CardInfo;
