import { useRef, useState, useEffect} from 'react';
import './LoginView.css';
const LOGIN_URL = 'http://localhost:8080/apiuser/signin';

const LoginView = () => {

    const userRef = useRef();
    const errRef = useRef();

    const [username, setUsername] = useState('');
    const [password, setpassword] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    }, [])
    const handleSubmit = async (e) => {
        e.preventDefault();
        const usuario = { username, password };
        try {
            const response = fetch(LOGIN_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(usuario)
            }).then(response => {
                if (response.data.accessToken) {
                  localStorage.setItem("user", JSON.stringify(response.data));
                }
                return response.data;
              });;
            console.log(JSON.stringify(response?.data));
            const accessToken = response?.data?.accessToken;
            const roles = response?.data?.roles;
            setUsername('');
            setPassword('');
            setSuccess(true);
        } catch (err) {
            console.log(err);
            if (!err?.response) {
                setErrMsg('No Server Response');
            } else if (err.response?.status === 400) {
                setErrMsg('Missing Username or Password');
            } else if (err.response?.status === 401) {
                setErrMsg('Unauthorized');
            } else {
                setErrMsg('Login Failed');
            }
            errRef.current.focus();
        }
    }

    return (
            <>
            {success ? (
                <section>
                    <h1>You are logged in!</h1>
                    <br />
                    <p>
                        <a href="/home">Go to Home</a>
                    </p>
                </section>
            ): (
                <div className="login-view">
                <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
                <div className="login-view-container">
                    <div className="login-view-header d-flex justify-content-center">
                        <h1>Login</h1>
                    </div>
                    <div className="login-view-body d-flex justify-content-center">
                        <form onSubmit={handleSubmit}>
                            <div className="form-group p-3">
                                <label htmlFor="username">Username</label>
                                <input 
                                type="text" 
                                className="form-control" 
                                id="username" 
                                aria-describedby="usernameHelp" 
                                placeholder="Enter username"
                                ref={userRef}
                                autoComplete="off"
                                onChange={(e) => setUsername(e.target.value)}
                                value={username}
                                required />
                                <small id="usernameHelp" className="form-text text-muted">We'll never share your username with anyone else.</small>
                            </div>
                            <div className="form-group">
                                <label htmlFor="password">Password</label>
                                <input 
                                type="password" 
                                className="form-control" 
                                id="password" 
                                placeholder="Password"
                                onChange={(e) => setpassword(e.target.value)}
                                value={password}
                                required />
                            </div>
                            <div className="form-group form-check">
                                <input type="checkbox" className="form-check-input" id="rememberMe" />
                                <label className="form-check-label" htmlFor="rememberMe">Remember me</label>
                            </div>
                            <button type="submit" className="btn btn-primary">Login</button>
                            <div className="login-view-footer">
                            <p>Don't have an account? <a href="/register">Register</a></p>
                            </div>

                        </form>

                        </div>
                    </div>
                </div>
                )
            }

            </>

        );
    }

export default LoginView;
