import './Cards.css';
import{useEffect, useState} from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import CardItemList from "../CardItemList";
const Cards = () => {
    const [cards, setCards] = useState([]);
    const [loading, setLoading] = useState(false);
    const [busqueda, setBusqueda] = useState();

    useEffect(() => {
        setLoading(true)
        if(busqueda === undefined){
            fetch('http://localhost:8080/apicartas/cartas')
        .then(response => response.json())
        .then(cards => setCards(cards))
        .then(() => setLoading(false));
        }else{
            fetch('http://localhost:8080/apicartas/cartas/buscarPorNombre?nombre='+busqueda)
        .then(response => response.json())
        .then(cards => setCards(cards))
        .then(() => setLoading(false));
        }
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        fetch('http://localhost:8080/apicartas/cartas/buscarPorNombre?nombre='+busqueda)
        .then(response => response.json())
        .then(cards => setCards(cards))
        .then(() => setLoading(false));
    }
    if(loading){
        return <h2>Loading....</h2>
    }
    return (
        <div className="cards">
            <div className="searchBar">
                <form onSubmit={handleSubmit}>
                    <Form>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Label>Busca por Nombre</Form.Label>
                            <Form.Control type="text" value={busqueda} placeholder="Buscar..." onChange={(e) => setBusqueda(e.target.value)} />
                        </Form.Group>
                        <Button variant="primary" type="submit">
                            Submit
                        </Button>
                    </Form>
                </form>
                
            </div>
            <h1 class="d-flex justify-content-center">Cartas</h1>
            <CardItemList cards={cards} />
        </div>
    );
}
export default Cards;
