import './CardInfo.css';
import { useParams} from 'react-router-dom';
import { useState, useEffect, } from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import ListGroupItem from 'react-bootstrap/ListGroupItem';
import ListGroup from 'react-bootstrap/ListGroup';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
const CardInfo = () => {
    const {cardId} = useParams();
    const [card, setCard] = useState({});
    useEffect(() => {
        fetch(`http://localhost:8080/apicartas/cartas/getCarta/${cardId}`)
        .then(response => response.json())
        .then(card => setCard(card));
    }, [cardId]);
    
    return (
        <div className="cardInfo" class="col-lg-3 d-flex justify-content-between">
            
            <Container>
                <Row>
                    <Col><Card.Img variant="left" src={card.imagen}/></Col>
                </Row>
                <Row>
                    <col>
                    <ListGroup className="list-group-flush">
                        <ListGroupItem>Codigo: {card.codigo}</ListGroupItem>
                        <ListGroupItem>Precio: {card.precio}â‚¬</ListGroupItem>
                        <ListGroupItem>Reprint: {
                        card.reprint === true ? card.reprint : 'Si'|| card.reprint === false ? card.reprint : 'No'
                        }</ListGroupItem>
                        <ListGroupItem>Palabras Clave: {card.keywords}</ListGroupItem>
                        <ListGroupItem>Descripcion: {card.descripcion}</ListGroupItem>
                        <ListGroupItem>Texto: {card.texto}</ListGroupItem>
                    </ListGroup>
                    <Button variant="primary" className="pt-2" href="" >Comprar</Button>
                    </col>
                </Row>
            </Container>
        </div>
    );
};

export default CardInfo;
